import * as AccordionPrimitive from "corvu/accordion";
import ChevronDownIcon from "lucide-solid/icons/chevron-down";
import { type ComponentProps, splitProps } from "solid-js";
import { cn } from "@/registry/jumpwind/lib/utils";

const useAccordion = AccordionPrimitive.useContext;
const useAccordionItem = AccordionPrimitive.useItemContext;
const useAccordionDisclosure = AccordionPrimitive.useDisclosureContext;

function Accordion(props: ComponentProps<typeof AccordionPrimitive.Root>) {
  return <AccordionPrimitive.Root data-slot="accordion" {...props} />;
}

function AccordionItem(props: ComponentProps<typeof AccordionPrimitive.Item>) {
  const [local, rest] = splitProps(props, ["class"]);

  return (
    <AccordionPrimitive.Item
      data-slot="accordion-item"
      class={cn("border-b last:border-b-0", local.class)}
      {...rest}
    />
  );
}

function AccordionTrigger(
  props: ComponentProps<typeof AccordionPrimitive.Trigger<"button">>,
) {
  const [local, rest] = splitProps(props, ["class", "children"]);

  return (
    <AccordionPrimitive.Trigger
      as="button"
      data-slot="accordion-trigger"
      class={cn(
        "flex flex-1 items-start justify-between gap-4 rounded-md py-4 text-left font-medium text-sm outline-none transition-all hover:underline focus-visible:border-ring focus-visible:ring-[3px] focus-visible:ring-ring/50 disabled:pointer-events-none disabled:opacity-50 [&[data-open]>svg]:rotate-180",
        local.class,
      )}
      {...rest}
    >
      {local.children}
      <ChevronDownIcon class="pointer-events-none size-4 shrink-0 translate-y-0.5 text-muted-foreground transition-transform duration-200" />
    </AccordionPrimitive.Trigger>
  );
}

function AccordionContent(
  props: ComponentProps<typeof AccordionPrimitive.Content<"div">>,
) {
  const [local, rest] = splitProps(props, ["class", "children"]);

  return (
    <AccordionPrimitive.Content
      as="div"
      data-slot="accordion-content"
      class="overflow-hidden text-sm data-closed:animate-accordion-up data-open:animate-accordion-down"
      {...rest}
    >
      <div class={cn("pt-0 pb-4", local.class)}>{local.children}</div>
    </AccordionPrimitive.Content>
  );
}

export {
  Accordion,
  AccordionItem,
  AccordionTrigger,
  AccordionContent,
  // Hooks
  useAccordion,
  useAccordionItem,
  useAccordionDisclosure,
};

import { type Accessor, createMemo, type Ref } from "solid-js";
import { type ClassValue, cnBase } from "tailwind-variants";

/**
 * Combines multiple class strings into one single string.
 *
 * @param classes - The classes to combine.
 * @returns The combined classes.
 */
export function cn(...classes: ClassValue[]): string | undefined {
  return cnBase(classes);
}

export type MaybeAccessor<T> = T | (() => T);

export type MaybeAccessorValue<T extends MaybeAccessor<unknown>> =
  T extends () => unknown ? ReturnType<T> : T;

export function access<T extends MaybeAccessor<unknown>>(
  v: T,
): MaybeAccessorValue<T> {
  return typeof v === "function" ? v() : (v as MaybeAccessorValue<T>);
}

export function chain<Args extends [] | unknown[]>(callbacks: {
  [Symbol.iterator]: () => IterableIterator<
    ((...args: Args) => unknown) | undefined
  >;
}): (...args: Args) => void {
  return (...args: Args) => {
    for (const callback of callbacks) callback?.(...args);
  };
}

export function mergeRefs<T>(...refs: Ref<T>[]): (el: T) => void {
  return chain(refs as ((el: T) => void)[]);
}

export function some(...signals: Accessor<unknown>[]) {
  return signals.some((signal) => !!signal());
}

/**
 * Returns a signal with the tag name of the element.
 *
 * @param props.element - The element to get the tag name of.
 * @param props.fallback - The fallback tag name to use if the element is `null`.
 * @returns ```typescript
 * Accessor<string>
 * ```
 */
export function createTagName(props: {
  element: MaybeAccessor<HTMLElement | null>;
  fallback: string;
}): Accessor<string> {
  const tagName = createMemo(
    () => access(props.element)?.tagName.toLowerCase() ?? props.fallback,
  );

  return tagName;
}
