import * as AlertDialogPrimitive from "corvu/dialog";
import { type ComponentProps, splitProps } from "solid-js";
import { cn } from "@/registry/jumpwind/lib/utils";
import {
  type ButtonVariantProps,
  buttonVariants,
} from "@/registry/jumpwind/ui/button";

const useAlertDialog = AlertDialogPrimitive.useContext;

function AlertDialog(props: ComponentProps<typeof AlertDialogPrimitive.Root>) {
  return <AlertDialogPrimitive.Root data-slot="alert-dialog" {...props} />;
}

function AlertDialogTrigger(
  props: ComponentProps<typeof AlertDialogPrimitive.Trigger<"button">>,
) {
  return (
    <AlertDialogPrimitive.Trigger
      as="button"
      data-slot="alert-dialog-trigger"
      {...props}
    />
  );
}

function AlertDialogPortal(
  props: ComponentProps<typeof AlertDialogPrimitive.Portal>,
) {
  return (
    <AlertDialogPrimitive.Portal data-slot="alert-dialog-portal" {...props} />
  );
}

function AlertDialogOverlay(
  props: ComponentProps<typeof AlertDialogPrimitive.Overlay<"div">>,
) {
  const [local, rest] = splitProps(props, ["class"]);

  return (
    <AlertDialogPrimitive.Overlay
      as="div"
      data-slot="alert-dialog-overlay"
      class={cn(
        "data-closed:fade-out-0 data-open:fade-in-0 fixed inset-0 z-50 bg-black/50 data-closed:animate-out data-open:animate-in",
        local.class,
      )}
      {...rest}
    />
  );
}

function AlertDialogContent(
  props: ComponentProps<typeof AlertDialogPrimitive.Content<"div">>,
) {
  const [local, rest] = splitProps(props, ["class"]);

  return (
    <AlertDialogPortal>
      <AlertDialogOverlay />
      <AlertDialogPrimitive.Content
        as="div"
        data-slot="alert-dialog-content"
        class={cn(
          "data-closed:fade-out-0 data-open:fade-in-0 data-closed:zoom-out-95 data-open:zoom-in-95 fixed top-[50%] left-[50%] z-50 grid w-full max-w-[calc(100%-2rem)] translate-x-[-50%] translate-y-[-50%] gap-4 rounded-lg border bg-background p-6 shadow-lg duration-200 data-closed:animate-out data-open:animate-in sm:max-w-lg",
          local.class,
        )}
        {...rest}
      />
    </AlertDialogPortal>
  );
}

function AlertDialogHeader(props: ComponentProps<"div">) {
  const [local, rest] = splitProps(props, ["class"]);

  return (
    <div
      data-slot="alert-dialog-header"
      class={cn("flex flex-col gap-2 text-center sm:text-left", local.class)}
      {...rest}
    />
  );
}

function AlertDialogFooter(props: ComponentProps<"div">) {
  const [local, rest] = splitProps(props, ["class"]);

  return (
    <div
      data-slot="alert-dialog-footer"
      class={cn(
        "flex flex-col-reverse gap-2 sm:flex-row sm:justify-end",
        local.class,
      )}
      {...rest}
    />
  );
}

function AlertDialogTitle(
  props: ComponentProps<typeof AlertDialogPrimitive.Label<"h2">>,
) {
  const [local, rest] = splitProps(props, ["class"]);

  return (
    <AlertDialogPrimitive.Label
      as="h2"
      data-slot="alert-dialog-title"
      class={cn("font-semibold text-lg", local.class)}
      {...rest}
    />
  );
}

function AlertDialogDescription(
  props: ComponentProps<typeof AlertDialogPrimitive.Description>,
) {
  const [local, rest] = splitProps(props, ["class"]);

  return (
    <AlertDialogPrimitive.Description
      data-slot="alert-dialog-description"
      class={cn("text-muted-foreground text-sm", local.class)}
      {...rest}
    />
  );
}

function AlertDialogAction(
  props: ComponentProps<"button"> & ButtonVariantProps,
) {
  const [local, rest] = splitProps(props, ["class", "size", "variant"]);

  return (
    <button
      data-slot="alert-dialog-action"
      class={buttonVariants({
        size: local.size,
        variant: local.variant,
        class: local.class,
      })}
      {...rest}
    />
  );
}

function AlertDialogCancel(
  props: ComponentProps<typeof AlertDialogPrimitive.Close<"button">>,
) {
  const [local, rest] = splitProps(props, ["class"]);

  return (
    <AlertDialogPrimitive.Close
      as="button"
      data-slot="alert-dialog-cancel"
      class={buttonVariants({ variant: "outline", class: local.class })}
      {...rest}
    />
  );
}

export {
  AlertDialog,
  AlertDialogPortal,
  AlertDialogOverlay,
  AlertDialogTrigger,
  AlertDialogContent,
  AlertDialogHeader,
  AlertDialogFooter,
  AlertDialogTitle,
  AlertDialogDescription,
  AlertDialogAction,
  AlertDialogCancel,
  // Hooks
  useAlertDialog,
};

import { type Accessor, createMemo, type Ref } from "solid-js";
import { type ClassValue, cnBase } from "tailwind-variants";

/**
 * Combines multiple class strings into one single string.
 *
 * @param classes - The classes to combine.
 * @returns The combined classes.
 */
export function cn(...classes: ClassValue[]): string | undefined {
  return cnBase(classes);
}

export type MaybeAccessor<T> = T | (() => T);

export type MaybeAccessorValue<T extends MaybeAccessor<unknown>> =
  T extends () => unknown ? ReturnType<T> : T;

export function access<T extends MaybeAccessor<unknown>>(
  v: T,
): MaybeAccessorValue<T> {
  return typeof v === "function" ? v() : (v as MaybeAccessorValue<T>);
}

export function chain<Args extends [] | unknown[]>(callbacks: {
  [Symbol.iterator]: () => IterableIterator<
    ((...args: Args) => unknown) | undefined
  >;
}): (...args: Args) => void {
  return (...args: Args) => {
    for (const callback of callbacks) callback?.(...args);
  };
}

export function mergeRefs<T>(...refs: Ref<T>[]): (el: T) => void {
  return chain(refs as ((el: T) => void)[]);
}

export function some(...signals: Accessor<unknown>[]) {
  return signals.some((signal) => !!signal());
}

/**
 * Returns a signal with the tag name of the element.
 *
 * @param props.element - The element to get the tag name of.
 * @param props.fallback - The fallback tag name to use if the element is `null`.
 * @returns ```typescript
 * Accessor<string>
 * ```
 */
export function createTagName(props: {
  element: MaybeAccessor<HTMLElement | null>;
  fallback: string;
}): Accessor<string> {
  const tagName = createMemo(
    () => access(props.element)?.tagName.toLowerCase() ?? props.fallback,
  );

  return tagName;
}

import {
  type ComponentProps,
  createMemo,
  splitProps,
  type ValidComponent,
} from "solid-js";
import { tv, type VariantProps } from "tailwind-variants";
import { createTagName } from "@/registry/jumpwind/lib/utils";
import { Dynamic, type DynamicProps } from "@/registry/jumpwind/ui/dynamic";

const BUTTON_INPUT_TYPES = [
  "button",
  "color",
  "file",
  "image",
  "reset",
  "submit",
] as const;

const buttonVariants = tv({
  base: "inline-flex shrink-0 items-center justify-center gap-2 whitespace-nowrap rounded-md font-medium text-sm outline-none transition-all focus-visible:border-ring focus-visible:ring-[3px] focus-visible:ring-ring/50 disabled:pointer-events-none disabled:opacity-50 aria-invalid:border-destructive aria-invalid:ring-destructive/20 dark:aria-invalid:ring-destructive/40 [&_svg:not([class*='size-'])]:size-4 [&_svg]:pointer-events-none [&_svg]:shrink-0",
  variants: {
    variant: {
      default:
        "bg-primary text-primary-foreground shadow-xs hover:bg-primary/90",
      destructive:
        "bg-destructive text-white shadow-xs hover:bg-destructive/90 focus-visible:ring-destructive/20 dark:bg-destructive/60 dark:focus-visible:ring-destructive/40",
      outline:
        "border bg-background shadow-xs hover:bg-accent hover:text-accent-foreground dark:border-input dark:bg-input/30 dark:hover:bg-input/50",
      secondary:
        "bg-secondary text-secondary-foreground shadow-xs hover:bg-secondary/80",
      ghost:
        "hover:bg-accent hover:text-accent-foreground dark:hover:bg-accent/50",
      link: "text-primary underline-offset-4 hover:underline",
    },
    size: {
      default: "h-9 px-4 py-2 has-[>svg]:px-3",
      sm: "h-8 gap-1.5 rounded-md px-3 has-[>svg]:px-2.5",
      lg: "h-10 rounded-md px-6 has-[>svg]:px-4",
      icon: "size-9",
    },
  },
  defaultVariants: {
    variant: "default",
    size: "default",
  },
});

type ButtonVariantProps = VariantProps<typeof buttonVariants>;

/** An accessible button that sets `type` and `role` properly based on if it's a native button. */
function Button<T extends ValidComponent = "button">(
  props: DynamicProps<T, ComponentProps<T>> &
    ButtonVariantProps & {
      type?: string;
    },
) {
  const [local, rest] = splitProps(props, [
    "class",
    "variant",
    "size",
    "type",
    "role",
  ]);

  const tagName = createTagName({
    element: () => rest.ref ?? null,
    fallback: "button",
  });

  const isButton = createMemo(() => {
    if (tagName() === "button") return true;
    if (tagName() === "input" && local.type !== undefined) {
      return BUTTON_INPUT_TYPES.indexOf(local.type) !== -1;
    }
    return false;
  });

  return (
    <Dynamic
      as="button"
      data-slot="button"
      type={local.type ?? (isButton() ? "button" : undefined)}
      role={local.role ?? (!isButton() ? "button" : undefined)}
      class={buttonVariants({
        variant: local.variant,
        size: local.size,
        class: local.class,
      })}
      {...rest}
    />
  );
}

export { Button, buttonVariants, type ButtonVariantProps };

