import * as HoverCardPrimitive from "@kobalte/core/hover-card";
import { type ComponentProps, mergeProps, splitProps } from "solid-js";
import { cn } from "@/registry/jumpwind/lib/utils";

function HoverCard(props: ComponentProps<typeof HoverCardPrimitive.Root>) {
  const defaultedProps = mergeProps(
    {
      placement: "bottom",
      gutter: 4,
    } satisfies HoverCardPrimitive.HoverCardRootProps,
    props,
  );
  const [local, rest] = splitProps(defaultedProps, ["placement", "gutter"]);

  return (
    <HoverCardPrimitive.Root
      data-slot="hover-card"
      gutter={local.gutter}
      placement={local.placement}
      {...rest}
    />
  );
}

function HoverCardTrigger(
  props: ComponentProps<typeof HoverCardPrimitive.Trigger<"a">>,
) {
  return (
    <HoverCardPrimitive.Trigger
      as="a"
      data-slot="hover-card-trigger"
      {...props}
    />
  );
}

function HoverCardContent(
  props: ComponentProps<typeof HoverCardPrimitive.Content<"div">>,
) {
  const [local, rest] = splitProps(props, ["class"]);

  return (
    <HoverCardPrimitive.Portal data-slot="hover-card-portal">
      <HoverCardPrimitive.Content
        as="div"
        data-slot="hover-card-content"
        class={cn(
          "data-closed:fade-out-0 data-expanded:fade-in-0 data-closed:zoom-out-95 data-expanded:zoom-in-95 data-[side=bottom]:slide-in-from-top-2 data-[side=left]:slide-in-from-right-2 data-[side=right]:slide-in-from-left-2 data-[side=top]:slide-in-from-bottom-2 z-50 w-64 origin-(--kb-hovercard-content-transform-origin) rounded-md border bg-popover p-4 text-popover-foreground shadow-md outline-hidden data-closed:animate-out data-expanded:animate-in",
          local.class,
        )}
        {...rest}
      />
    </HoverCardPrimitive.Portal>
  );
}

export { HoverCard, HoverCardTrigger, HoverCardContent };

import { type Accessor, createMemo, type Ref } from "solid-js";
import { type ClassValue, cnBase } from "tailwind-variants";

/**
 * Combines multiple class strings into one single string.
 *
 * @param classes - The classes to combine.
 * @returns The combined classes.
 */
export function cn(...classes: ClassValue[]): string | undefined {
  return cnBase(classes);
}

export type MaybeAccessor<T> = T | (() => T);

export type MaybeAccessorValue<T extends MaybeAccessor<unknown>> =
  T extends () => unknown ? ReturnType<T> : T;

export function access<T extends MaybeAccessor<unknown>>(
  v: T,
): MaybeAccessorValue<T> {
  return typeof v === "function" ? v() : (v as MaybeAccessorValue<T>);
}

export function chain<Args extends [] | unknown[]>(callbacks: {
  [Symbol.iterator]: () => IterableIterator<
    ((...args: Args) => unknown) | undefined
  >;
}): (...args: Args) => void {
  return (...args: Args) => {
    for (const callback of callbacks) callback?.(...args);
  };
}

export function mergeRefs<T>(...refs: Ref<T>[]): (el: T) => void {
  return chain(refs as ((el: T) => void)[]);
}

export function some(...signals: Accessor<unknown>[]) {
  return signals.some((signal) => !!signal());
}

/**
 * Returns a signal with the tag name of the element.
 *
 * @param props.element - The element to get the tag name of.
 * @param props.fallback - The fallback tag name to use if the element is `null`.
 * @returns ```typescript
 * Accessor<string>
 * ```
 */
export function createTagName(props: {
  element: MaybeAccessor<HTMLElement | null>;
  fallback: string;
}): Accessor<string> {
  const tagName = createMemo(
    () => access(props.element)?.tagName.toLowerCase() ?? props.fallback,
  );

  return tagName;
}
