import * as RadioGroupPrimitive from "@kobalte/core/radio-group";
import DotIcon from "lucide-solid/icons/dot";
import { type ComponentProps, splitProps } from "solid-js";
import { cn } from "@/registry/jumpwind/lib/utils";

const RadioGroupLabel = RadioGroupPrimitive.Label;
const RadioGroupDescription = RadioGroupPrimitive.Description;
const RadioGroupMessage = RadioGroupPrimitive.ErrorMessage;

export interface RadioGroupItemValue<TValue = string> {
  value: TValue;
  label: string;
}

function RadioGroupRoot(
  props: ComponentProps<typeof RadioGroupPrimitive.Root<"div">>,
) {
  const [local, rest] = splitProps(props, ["class"]);

  return (
    <RadioGroupPrimitive.Root
      as="div"
      data-slot="radio-group"
      class={cn("grid gap-3", local.class)}
      {...rest}
    />
  );
}

function RadioGroupItem(
  props: ComponentProps<typeof RadioGroupPrimitive.Item<"div">>,
) {
  const [local, rest] = splitProps(props, ["class"]);

  return (
    <RadioGroupPrimitive.Item
      as="div"
      data-slot="radio-group-item"
      class={cn("flex items-center gap-x-2", local.class)} // TODO: verify these styles (in solid-ui, shadcn)
      {...rest}
    />
  );
}

function RadioGroupItemInput(
  props: ComponentProps<typeof RadioGroupPrimitive.ItemInput<"input">>,
) {
  return (
    <RadioGroupPrimitive.ItemInput
      as="input"
      data-slot="radio-group-input"
      {...props}
    />
  );
}

function RadioGroupItemControl(
  props: ComponentProps<typeof RadioGroupPrimitive.ItemControl<"div">>,
) {
  const [local, rest] = splitProps(props, ["class"]);

  return (
    <RadioGroupPrimitive.ItemControl
      as="div"
      data-slot="radio-group-control"
      class={cn(
        // "border-input text-primary focus-visible:border-ring focus-visible:ring-ring/50 aria-invalid:ring-destructive/20 dark:aria-invalid:ring-destructive/40 aria-invalid:border-destructive dark:bg-input/30 aspect-square size-4 shrink-0 rounded-full border shadow-xs transition-[color,box-shadow] outline-none focus-visible:ring-[3px] disabled:cursor-not-allowed disabled:opacity-50",
        "flex aspect-square h-4 w-4 items-center justify-center rounded-full border border-primary text-primary shadow transition-shadow focus:outline-none focus-visible:ring-[1.5px] focus-visible:ring-ring disabled:cursor-not-allowed disabled:opacity-50 data-[checked]:bg-foreground",
        local.class,
      )}
      {...rest}
    >
      <RadioGroupPrimitive.ItemIndicator
        data-slot="radio-group-indicator"
        class="size-2 rounded-full data-checked:bg-red-300 data-checked:text-red-300"
      >
        <DotIcon class="size-2" />
      </RadioGroupPrimitive.ItemIndicator>
    </RadioGroupPrimitive.ItemControl>
  );
}

function RadioGroupItemLabel(
  props: ComponentProps<typeof RadioGroupPrimitive.ItemLabel<"label">>,
) {
  const [local, rest] = splitProps(props, ["class"]);

  return (
    <RadioGroupPrimitive.ItemLabel
      as="label"
      data-slot="radio-group-item-label"
      class={cn(
        "font-medium text-primary text-sm leading-none peer-disabled:cursor-not-allowed peer-disabled:opacity-70",
        local.class,
      )}
      {...rest}
    />
  );
}

export {
  RadioGroupRoot,
  RadioGroupItem,
  RadioGroupItemLabel,
  RadioGroupItemInput,
  RadioGroupItemControl,
  // Forms
  RadioGroupLabel,
  RadioGroupDescription,
  RadioGroupMessage,
};

import { type Accessor, createMemo, type Ref } from "solid-js";
import { type ClassValue, cnBase } from "tailwind-variants";

/**
 * Combines multiple class strings into one single string.
 *
 * @param classes - The classes to combine.
 * @returns The combined classes.
 */
export function cn(...classes: ClassValue[]): string | undefined {
  return cnBase(classes);
}

export type MaybeAccessor<T> = T | (() => T);

export type MaybeAccessorValue<T extends MaybeAccessor<unknown>> =
  T extends () => unknown ? ReturnType<T> : T;

export function access<T extends MaybeAccessor<unknown>>(
  v: T,
): MaybeAccessorValue<T> {
  return typeof v === "function" ? v() : (v as MaybeAccessorValue<T>);
}

export function chain<Args extends [] | unknown[]>(callbacks: {
  [Symbol.iterator]: () => IterableIterator<
    ((...args: Args) => unknown) | undefined
  >;
}): (...args: Args) => void {
  return (...args: Args) => {
    for (const callback of callbacks) callback?.(...args);
  };
}

export function mergeRefs<T>(...refs: Ref<T>[]): (el: T) => void {
  return chain(refs as ((el: T) => void)[]);
}

export function some(...signals: Accessor<unknown>[]) {
  return signals.some((signal) => !!signal());
}

/**
 * Returns a signal with the tag name of the element.
 *
 * @param props.element - The element to get the tag name of.
 * @param props.fallback - The fallback tag name to use if the element is `null`.
 * @returns ```typescript
 * Accessor<string>
 * ```
 */
export function createTagName(props: {
  element: MaybeAccessor<HTMLElement | null>;
  fallback: string;
}): Accessor<string> {
  const tagName = createMemo(
    () => access(props.element)?.tagName.toLowerCase() ?? props.fallback,
  );

  return tagName;
}
