const SIDEBAR_COOKIE_NAME = "sidebar:state";
const SIDEBAR_COOKIE_MAX_AGE = 60 * 60 * 24 * 7;
const SIDEBAR_WIDTH = "16rem";
const SIDEBAR_WIDTH_ICON = "3rem";
const SIDEBAR_WIDTH_MOBILE = "18rem";
const SIDEBAR_KEYBOARD_SHORTCUT = "b";

export interface SidebarDataSet {
  "data-expanded": string | undefined;
  "data-closed": string | undefined;
}

export type SidebarContextValue = {
  dataset: Accessor<SidebarDataSet>;
  open: Accessor<boolean>;
  setOpen: (open: boolean) => void;
  isMobile: Accessor<boolean>;
  toggle: () => void;
};

export const SidebarContext = createContext<SidebarContextValue>();

export function useSidebarContext() {
  const context = useContext(SidebarContext);
  if (!context) {
    throw new Error(
      "`useSidebarContext` must be used within a `SidebarProvider`.",
    );
  }

  return context;
}

export function SidebarProvider(
  props: Omit<ComponentProps<"div">, "style"> & {
    defaultOpen?: boolean;
    open?: boolean;
    onOpenChange?: (open: boolean) => void;
    style?: JSX.CSSProperties;
  },
) {
  const defaulted = mergeProps(
    { defaultOpen: true } satisfies typeof props,
    props,
  );

  const [local, rest] = splitProps(defaulted, [
    "defaultOpen",
    "open",
    "onOpenChange",
    "class",
    "style",
    "children",
  ]);

  const [open, setOpen] = createControllableSignal<boolean>({
    initialValue: local.defaultOpen,
    value: () => local.open,
    onChange: (value) => {
      local.onOpenChange?.(value);
      document.cookie = `${SIDEBAR_COOKIE_NAME}=${value}; path=/; max-age=${SIDEBAR_COOKIE_MAX_AGE}`;
    },
  });

  const toggle = () => setOpen((open) => !open);

  // Adds a keyboard shortcut to toggle the sidebar.
  createEffect(() => {
    const handleKeyDown = (event: KeyboardEvent) => {
      if (
        (event.metaKey || event.ctrlKey) &&
        event.key === SIDEBAR_KEYBOARD_SHORTCUT
      ) {
        event.preventDefault();
        toggle();
      }
    };

    window.addEventListener("keydown", handleKeyDown);

    onCleanup(() => {
      window.removeEventListener("keydown", handleKeyDown);
    });
  });

  const isMobile = useIsMobile();

  const dataset: Accessor<SidebarDataSet> = createMemo(() => ({
    "data-expanded": open() ? "" : undefined,
    "data-closed": !open() ? "" : undefined,
  }));

  const contextValue: SidebarContextValue = {
    dataset,
    open,
    setOpen,
    isMobile,
    toggle,
  };

  return (
    <SidebarContext.Provider value={contextValue}>
      <div
        style={{
          "--sidebar-width": SIDEBAR_WIDTH,
          "--sidebar-width-icon": SIDEBAR_WIDTH_ICON,
          ...local.style,
        }}
        class={cn(
          "group/sidebar-wrapper flex min-h-svh w-full has-[[data-variant=inset]]:bg-sidebar",
          local.class,
        )}
        {...rest}
      >
        {local.children}
      </div>
    </SidebarContext.Provider>
  );
}

export function Sidebar(
  props: ComponentProps<"div"> & {
    side?: "left" | "right";
    variant?: "sidebar" | "floating" | "inset";
    collapsible?: "offcanvas" | "icon" | "none";
  },
) {
  const defaulted = mergeProps(
    {
      side: "left",
      variant: "sidebar",
      collapsible: "offcanvas",
    } satisfies typeof props,
    props,
  );

  const [local, rest] = splitProps(defaulted, [
    "side",
    "variant",
    "collapsible",
    "class",
    "children",
  ]);

  const context = useSidebarContext();

  return (
    <Switch
      fallback={
        <div
          class="group peer hidden text-sidebar-foreground md:block"
          {...context.dataset()}
          data-variant={local.variant}
          data-side={local.side}
        >
          {/* This is what handles the sidebar gap on desktop */}
          <div
            class={cn(
              "relative h-svh w-[--sidebar-width] bg-transparent transition-[width] duration-200 ease-linear",
              "group-data-[collapsible=offcanvas]:w-0",
              "group-data-[side=right]:rotate-180",
              local.variant === "floating" || local.variant === "inset"
                ? "group-data-[collapsible=icon]:w-[calc(var(--sidebar-width-icon)_+_theme(spacing.4))]"
                : "group-data-[collapsible=icon]:w-[--sidebar-width-icon]",
            )}
          />
          <div
            class={cn(
              "fixed inset-y-0 z-10 hidden h-svh w-[--sidebar-width] transition-[left,right,width] duration-200 ease-linear md:flex",
              local.side === "left"
                ? "left-0 group-data-[collapsible=offcanvas]:left-[calc(var(--sidebar-width)*-1)]"
                : "right-0 group-data-[collapsible=offcanvas]:right-[calc(var(--sidebar-width)*-1)]",
              // Adjust the padding for floating and inset variants.
              local.variant === "floating" || local.variant === "inset"
                ? "p-2 group-data-[collapsible=icon]:w-[calc(var(--sidebar-width-icon)_+_theme(spacing.4)_+2px)]"
                : "group-data-[collapsible=icon]:w-[--sidebar-width-icon] group-data-[side=left]:border-r group-data-[side=right]:border-l",
              local.class,
            )}
            {...rest}
          >
            <div
              data-sidebar="sidebar"
              class="flex h-full w-full flex-col bg-sidebar group-data-[variant=floating]:rounded-lg group-data-[variant=floating]:border group-data-[variant=floating]:border-sidebar-border group-data-[variant=floating]:shadow"
            >
              {local.children}
            </div>
          </div>
        </div>
      }
    >
      <Match when={local.collapsible === "none"}>
        <div
          class={cn(
            "flex h-full w-[--sidebar-width] flex-col bg-sidebar text-sidebar-foreground",
            local.class,
          )}
          {...rest}
        >
          {local.children}
        </div>
      </Match>

      <Match when={context.isMobile()}>
        <Drawer
          open={context.open()}
          onOpenChange={context.setOpen}
          side={local.side}
        >
          <DrawerContent
            data-sidebar="sidebar"
            data-mobile="true"
            class="w-[--sidebar-width] bg-sidebar p-0 text-sidebar-foreground [&>button]:hidden"
            style={{
              "--sidebar-width": SIDEBAR_WIDTH_MOBILE,
            }}
          >
            <div class="flex h-full w-full flex-col">{local.children}</div>
          </DrawerContent>
        </Drawer>
      </Match>
    </Switch>
  );
}

export function SidebarTrigger(props: ComponentProps<typeof Button<"button">>) {
  const [local, rest] = splitProps(props, ["class", "onClick"]);

  const context = useSidebarContext();

  return (
    <Button<"button">
      data-trigger="trigger"
      variant="ghost"
      size="icon"
      class={cn("size-7", local.class)}
      onClick={(event) => {
        if (typeof local.onClick === "function") {
          local.onClick?.(event);
        } else {
          local.onClick?.[1](event);
        }
        context.toggle();
      }}
      {...rest}
    >
      <Show
        when={!open()}
        fallback={
          <PanelLeftCloseIcon class="size-4">
            <title>Close sidebar</title>
          </PanelLeftCloseIcon>
        }
      >
        <PanelLeftOpenIcon class="size-4">
          <title>Open sidebar</title>
        </PanelLeftOpenIcon>
      </Show>
    </Button>
  );
}

