import * as SwitchPrimitive from "@kobalte/core/switch";
import { type ComponentProps, splitProps } from "solid-js";
import { cn } from "@/registry/jumpwind/lib/utils";

const SwitchLabel = SwitchPrimitive.Label;
const SwitchDescription = SwitchPrimitive.Description;
const SwitchMessage = SwitchPrimitive.ErrorMessage;

function SwitchRoot(props: ComponentProps<typeof SwitchPrimitive.Root<"div">>) {
  const [local, rest] = splitProps(props, ["class"]);

  return (
    <SwitchPrimitive.Root
      as="div"
      data-slot="switch"
      class={cn("group relative flex items-start space-x-2", local.class)}
      {...rest}
    />
  );
}

function SwitchInput(
  props: ComponentProps<typeof SwitchPrimitive.Input<"input">>,
) {
  const [local, rest] = splitProps(props, ["class"]);

  return (
    <SwitchPrimitive.Input
      as="input"
      data-slot="switch-input"
      class={cn("peer", local.class)}
      {...rest}
    />
  );
}

function SwitchControl(
  props: ComponentProps<typeof SwitchPrimitive.Control<"div">>,
) {
  const [local, rest] = splitProps(props, ["class"]);

  return (
    <SwitchPrimitive.Control
      as="div"
      data-slot="switch-control"
      class={cn(
        "peer inline-flex h-[1.15rem] w-8 shrink-0 items-center rounded-full border border-transparent not-data-checked:bg-input shadow-xs outline-none transition-all focus-visible:border-ring focus-visible:ring-[3px] focus-visible:ring-ring/50 disabled:cursor-not-allowed disabled:opacity-50 data-checked:bg-primary dark:not-data-checked:bg-input/80",
        local.class,
      )}
      {...rest}
    >
      <SwitchPrimitive.Thumb
        data-slot="switch-thumb"
        class={cn(
          "pointer-events-none block size-4 not-data-checked:translate-x-0 rounded-full bg-background ring-0 transition-transform data-checked:translate-x-[calc(100%-2px)] dark:not-data-checked:bg-foreground dark:data-checked:bg-primary-foreground",
        )}
      />
    </SwitchPrimitive.Control>
  );
}

export {
  SwitchRoot,
  SwitchInput,
  SwitchControl,
  // Forms
  SwitchLabel,
  SwitchDescription,
  SwitchMessage,
};

import { type Accessor, createMemo, type Ref } from "solid-js";
import { type ClassValue, cnBase } from "tailwind-variants";

/**
 * Combines multiple class strings into one single string.
 *
 * @param classes - The classes to combine.
 * @returns The combined classes.
 */
export function cn(...classes: ClassValue[]): string | undefined {
  return cnBase(classes);
}

export type MaybeAccessor<T> = T | (() => T);

export type MaybeAccessorValue<T extends MaybeAccessor<unknown>> =
  T extends () => unknown ? ReturnType<T> : T;

export function access<T extends MaybeAccessor<unknown>>(
  v: T,
): MaybeAccessorValue<T> {
  return typeof v === "function" ? v() : (v as MaybeAccessorValue<T>);
}

export function chain<Args extends [] | unknown[]>(callbacks: {
  [Symbol.iterator]: () => IterableIterator<
    ((...args: Args) => unknown) | undefined
  >;
}): (...args: Args) => void {
  return (...args: Args) => {
    for (const callback of callbacks) callback?.(...args);
  };
}

export function mergeRefs<T>(...refs: Ref<T>[]): (el: T) => void {
  return chain(refs as ((el: T) => void)[]);
}

export function some(...signals: Accessor<unknown>[]) {
  return signals.some((signal) => !!signal());
}

/**
 * Returns a signal with the tag name of the element.
 *
 * @param props.element - The element to get the tag name of.
 * @param props.fallback - The fallback tag name to use if the element is `null`.
 * @returns ```typescript
 * Accessor<string>
 * ```
 */
export function createTagName(props: {
  element: MaybeAccessor<HTMLElement | null>;
  fallback: string;
}): Accessor<string> {
  const tagName = createMemo(
    () => access(props.element)?.tagName.toLowerCase() ?? props.fallback,
  );

  return tagName;
}
