import * as ToggleGroupPrimitive from "@kobalte/core/toggle-group";
import {
  type ComponentProps,
  createContext,
  splitProps,
  useContext,
} from "solid-js";
import { cn } from "@/registry/jumpwind/lib/utils";
import {
  type ToggleVariantProps,
  toggleVariants,
} from "@/registry/jumpwind/ui/toggle";

const ToggleGroupContext = createContext<ToggleVariantProps>({
  size: "default",
  variant: "default",
});

function useToggleGroup() {
  const context = useContext(ToggleGroupContext);
  if (!context) {
    throw new Error("useToggleGroup must be used within a ToggleGroup");
  }
  return context;
}

function ToggleGroup(
  props: ComponentProps<typeof ToggleGroupPrimitive.Root<"div">> &
    ToggleVariantProps,
) {
  const [local, rest] = splitProps(props, [
    "class",
    "variant",
    "size",
    "children",
  ]);

  return (
    <ToggleGroupPrimitive.Root
      as="div"
      data-slot="toggle-group"
      data-size={local.size}
      data-variant={local.variant}
      class={cn(
        "group/toggle-group flex w-fit items-center rounded-md data-[variant=outline]:shadow-xs",
        local.class,
      )}
      {...rest}
    >
      <ToggleGroupContext.Provider
        value={{
          variant: local.variant,
          size: local.size,
        }}
      >
        {local.children}
      </ToggleGroupContext.Provider>
    </ToggleGroupPrimitive.Root>
  );
}

function ToggleGroupItem(
  props: ComponentProps<typeof ToggleGroupPrimitive.Item<"button">> &
    ToggleVariantProps,
) {
  const [local, rest] = splitProps(props, [
    "class",
    "variant",
    "size",
    "children",
  ]);

  const context = useContext(ToggleGroupContext);
  const size = () => context?.size ?? local.size;
  const variant = () => context?.variant ?? local.variant;

  return (
    <ToggleGroupPrimitive.Item
      as="button"
      data-slot="toggle-group-item"
      data-size={size()}
      data-variant={variant()}
      class={toggleVariants({
        size: size(),
        variant: variant(),
        class: [
          "min-w-0 flex-1 shrink-0 rounded-none shadow-none first:rounded-l-md last:rounded-r-md focus:z-10 focus-visible:z-10 data-[variant=outline]:border-l-0 data-[variant=outline]:first:border-l",
          local.class,
        ],
      })}
      {...rest}
    >
      {local.children}
    </ToggleGroupPrimitive.Item>
  );
}

export {
  ToggleGroup,
  ToggleGroupItem,
  // Hooks
  useToggleGroup,
};

import { type Accessor, createMemo, type Ref } from "solid-js";
import { type ClassValue, cnBase } from "tailwind-variants";

/**
 * Combines multiple class strings into one single string.
 *
 * @param classes - The classes to combine.
 * @returns The combined classes.
 */
export function cn(...classes: ClassValue[]): string | undefined {
  return cnBase(classes);
}

export type MaybeAccessor<T> = T | (() => T);

export type MaybeAccessorValue<T extends MaybeAccessor<unknown>> =
  T extends () => unknown ? ReturnType<T> : T;

export function access<T extends MaybeAccessor<unknown>>(
  v: T,
): MaybeAccessorValue<T> {
  return typeof v === "function" ? v() : (v as MaybeAccessorValue<T>);
}

export function chain<Args extends [] | unknown[]>(callbacks: {
  [Symbol.iterator]: () => IterableIterator<
    ((...args: Args) => unknown) | undefined
  >;
}): (...args: Args) => void {
  return (...args: Args) => {
    for (const callback of callbacks) callback?.(...args);
  };
}

export function mergeRefs<T>(...refs: Ref<T>[]): (el: T) => void {
  return chain(refs as ((el: T) => void)[]);
}

export function some(...signals: Accessor<unknown>[]) {
  return signals.some((signal) => !!signal());
}

/**
 * Returns a signal with the tag name of the element.
 *
 * @param props.element - The element to get the tag name of.
 * @param props.fallback - The fallback tag name to use if the element is `null`.
 * @returns ```typescript
 * Accessor<string>
 * ```
 */
export function createTagName(props: {
  element: MaybeAccessor<HTMLElement | null>;
  fallback: string;
}): Accessor<string> {
  const tagName = createMemo(
    () => access(props.element)?.tagName.toLowerCase() ?? props.fallback,
  );

  return tagName;
}

import * as TogglePrimitive from "@kobalte/core/toggle-button";
import { type ComponentProps, splitProps } from "solid-js";
import { tv, type VariantProps } from "tailwind-variants";

const toggleVariants = tv({
  base: "inline-flex items-center justify-center gap-2 rounded-md text-sm font-medium hover:bg-muted hover:text-muted-foreground disabled:pointer-events-none disabled:opacity-50 data-[state=on]:bg-accent data-[state=on]:text-accent-foreground [&_svg]:pointer-events-none [&_svg:not([class*='size-'])]:size-4 [&_svg]:shrink-0 focus-visible:border-ring focus-visible:ring-ring/50 focus-visible:ring-[3px] outline-none transition-[color,box-shadow] aria-invalid:ring-destructive/20 dark:aria-invalid:ring-destructive/40 aria-invalid:border-destructive whitespace-nowrap",
  variants: {
    variant: {
      default: "bg-transparent",
      outline:
        "border border-input bg-transparent shadow-xs hover:bg-accent hover:text-accent-foreground",
    },
    size: {
      default: "h-9 px-2 min-w-9",
      sm: "h-8 px-1.5 min-w-8",
      lg: "h-10 px-2.5 min-w-10",
    },
  },
  defaultVariants: {
    variant: "default",
    size: "default",
  },
});

export type ToggleVariantProps = VariantProps<typeof toggleVariants>;

function Toggle(
  props: ComponentProps<typeof TogglePrimitive.Root<"button">> &
    ToggleVariantProps,
) {
  const [local, rest] = splitProps(props, ["class", "variant", "size"]);

  return (
    <TogglePrimitive.Root
      as="button"
      data-slot="toggle"
      class={toggleVariants({
        variant: local.variant,
        size: local.size,
        class: local.class,
      })}
      {...rest}
    />
  );
}

export {
  Toggle,
  // Hooks
  toggleVariants,
};
